# Dotfiles environment settings
# Tip: toggle auto-upgrades (0/1). Default is safe (no auto-upgrade).
export DOTFILES_STARTUP_AUTO_UPGRADE=0

# How often (in hours) the startup checks/upgrades should run.
export DOTFILES_STARTUP_INTERVAL_HOURS=24

# Fonts during bootstrap (Linux + Windows via WSL)
# Set to 0 to skip font installation when running bootstrap.sh
export DOTFILES_INSTALL_FONTS=1

# You can add other global env here as needed, e.g.:
# export EDITOR="nvim"
# export NVIM_CHANNEL="stable"   # (if you later want to use this in your scripts)
